const mint = async () => {
    const abi = Token.abi;
    const charge = 100;
    console.log(charge, "=========MINT=========");
    const contractAddress = "0xd5E0e18F72f450054d385850dc8B6aB47E294de7"
    const provider = new ethers.providers.Web3Provider(window.ethereum);
  
    const signer = await provider.getSigner();
    const address = await signer.getAddress();
    const bounceContract = new ethers.Contract(contractAddress, abi, signer)
  
    await (await bounceContract.mint(address, ethers.utils.parseUnits(charge.toString(), 18))).wait();
    // await (await bounceContract.transfer(address, ethers.utils.parseUnits(charge.toString(), 18))).wait();
  
  }

  
const deposit = async (amount) => {
    const abi = Token.abi;
    const charge = amount;
    console.log(charge, "=========DEPOSIT=========");
    const contractAddress = "0xd5E0e18F72f450054d385850dc8B6aB47E294de7"
    const provider = new ethers.providers.Web3Provider(window.ethereum);
  
    const signer = await provider.getSigner();
    const address = await signer.getAddress();
    const bounceContract = new ethers.Contract(contractAddress, abi, signer)
  
    await (await bounceContract.transfer(address, ethers.utils.parseUnits(charge.toString(), 18))).wait();
    // await (await bounceContract.transfer(address, ethers.utils.parseUnits(charge.toString(), 18))).wait();
  
  }

